css:
@media (min-width: 992px) {

 body {
   font-family: 'Montserrat', sans-serif !important;
  }

  .header__toggler {
    display: none;
}

  main .header__nav a {
    text-transform: none;
    color: #ffffff;
    font-weight: 500;
    margin: 0px 20px;
  }

  .banner-slider {
    max-width: 100%;
    max-height: calc(100rem*var(--banner-aspect-ratio));
}


  main .header__nav a:hover {
    color: #009eff;
  }

.header {
    background: url(../img/uploads/bg.jpg) no-repeat center top;
    height: 236px !important;
}
.header__logo {
    max-width: 294px;
    max-height: 132px;
    margin-top: -51px;
    margin-left: 20px;
}

.header__nav {
    width: 100%;
    position: absolute;
    left: -360px;
    margin-top: 84px;
}

.top-bar__countdown {
    font-size: 15px;
    font-weight: 700;
    text-transform: uppercase;
    background: #0490e6;
  }

  .header__row {
    min-height: 170px;
}

#new-menu {
    position: absolute;
    width: 100%;
    margin-top: 120px;
}

.whats-top {
  background: url(../img/uploads/whats.png) no-repeat left top;
  width: 165px;
  height: 40px;
  font-size: 14px;
  font-weight: 700;
  color: #ffffff;
  padding: 10px 0px 0px 28px;
  float: left;
  z-index: 999;
  margin-left: 20px;
}

#search-bar {
      margin-top: 187px;
      margin-left: 796px;
      position: absolute;
      }

  main #search-input {
  font-size:13px;}

#new-menu .container {
    padding-left: 291px;
    display: flex;
}

  .top-bar__nav {
  display:none !important;}

  .icon-top {}

.icon-top a {
    float: left;
    font-size: 13px;
    color: #ffffff;
    padding: 4px 15px 0 15px;
    font-weight: 400;
    font-family: 'Montserrat',serif;
    text-transform: none;
    z-index: 999;
    position: relative;
}

  main #cart-button .fa-shopping-bag:before {
    background-color: #ffffff;
}

  main #user-button .fa-user:before {
    background-color: #ffffff;
    margin-bottom: -11px;
}

  #user-button {
    margin-top:-7px;
}

  #user-button:after {
    content: "Entrar ou Registrar-se";
    font-size: 12px;
    color:#fff;
}

  .banner-centro {background: url(../img/uploads/bg-center.png) no-repeat center top !important;height: 423px;z-index: 2;padding-top: 110px;text-align: center;z-index: 10;position: relative;margin-top: 40px;}

.custom-html .banner-centro .container:first-child {
    max-width: 1280px;
}

.mini-banner {
    float: left;
    padding: 15px 12px;
}

  #page-home .collection-shelf {
    background: #e8e8e8;
}

.products-carousel__title:before {
    background:transparent;
}

.products-carousel__title a, .products-carousel__title span {
    background: transparent;
    font-weight: 700;
    font-size: 28px;
    padding: 5px 0;
    text-transform: uppercase;
    color: #000070;
    border-bottom: solid 3px #009eff;
}

.products-carousel .products-carousel__item {
    background: #fff;
}

#page-home .products-carousel {
    background: transparent;
    padding: 2rem 4rem;
}

  .rounded-icon {    border: 2px solid #007aff;}

  main .info-bar__item {
    color: #000070;
  }

  main .info-bar {
height: 485px;
    margin-bottom: -425px;
    padding-top: 30px;
    margin-top: 90px;
    position: relative;
    max-width: 100%;
    background: url(../img/uploads/bg-center.jpg) no-repeat center top;
}

  .sections .custom-html:nth-child(3) {
    position: relative;
}
.sections .custom-html:nth-child(3) .container {
    max-width: 1280px;
    display: flex;
}

}
